mapper.select:
SELECT A.iboard, A.icategory, A.title, A.ctnt, A.iuser, A.rdt, A.hits, A.lastip, B.nm AS writernm, B.profileimg, C.icategory, C.nm AS categorynm
FROM t_board A INNER JOIN t_user B ON A.iuser = B.iuser LEFT JOIN t_board_category C ON A.icategory = C.icategory
WHERE A.isdel = 0 AND A.iboard = ${iboard}

mapper.update:
UPDATE t_board
SET hits = hits + 1
WHERE iboard = ${iboard}
